# This file is executed on every boot (including wake-boot from deepsleep)#import esp#esp.osdebug(None)#import webrepl#webrepl.start()import uos as osconfig_folder = "configs"wifi_config_file = "wifi.conf"if config_folder not in os.listdir("."):  print("configs folder not found creating")  os.mkdir("configs")  if wifi_config_file not in os.listdir("configs"):  print("{0} not found creating".format(wifi_config_file))  content = """{"ssid": " ", "ssid_pass": " ", "mqtt_borker": " ", "low_power_mode": false, "publish_interval": 30}"""  f = open(config_folder + '/' + wifi_config_file, "w")  f.write(content)  f.close()